// Function to create and show the settings menu
function showSettingsMenu() {
    // If the menu is already visible, remove it
    if (menuVisible) {
        settingsMenu.remove();
        menuVisible = false;
        return;
    }

    // Create the container
    const container = document.createElement('div');
    container.style.position = 'fixed';
    container.style.top = '25%';
    container.style.left = '50%';
    container.style.transform = 'translate(-50%, -50%)';
    container.style.backgroundColor = 'black';
    container.style.border = '2px solid white';
    container.style.padding = '10px';
    container.style.zIndex = 10000;
    container.style.color = 'white';
    container.style.fontFamily = 'CustomFont';
    container.style.width = '400px';

    // Create the title
    const title = document.createElement('h3');
    title.textContent = 'Настройки RussianCards';
    title.style.textAlign = 'center';
    container.appendChild(title);

    // Create containers for left and right columns
    const leftColumn = document.createElement('div');
    const rightColumn = document.createElement('div');
    leftColumn.style.float = 'left';
    rightColumn.style.float = 'right';
    leftColumn.style.width = '45%';
    rightColumn.style.width = '45%';
    leftColumn.style.marginRight = '5%';
    container.appendChild(leftColumn);
    container.appendChild(rightColumn);

    // Function to create a setting item
    function createSettingItem(key, label, tooltipText, column) {
        const settingContainer = document.createElement('div');
        settingContainer.style.marginBottom = '10px';

        const settingLabel = document.createElement('label');
        settingLabel.textContent = label + ':';
        settingLabel.style.marginRight = '10px';
        settingLabel.style.cursor = 'pointer';

        // Create the tooltip
        const tooltip = document.createElement('div');
        tooltip.className = 'custom-tooltip';
        tooltip.innerHTML = tooltipText;
        tooltip.style.fontFamily = 'CustomFont';
        document.body.appendChild(tooltip);

        // Event listeners for showing/hiding the tooltip and following the cursor
        settingLabel.addEventListener('mouseover', () => {
            tooltip.style.display = 'block';
        });
        settingLabel.addEventListener('mousemove', (event) => {
            tooltip.style.left = event.pageX + 10 + 'px';
            tooltip.style.top = event.pageY + 10 + 'px';
        });

        settingLabel.addEventListener('mouseleave', () => {
            tooltip.style.display = 'none';
        });

        tooltip.addEventListener('mouseenter', () => {
            tooltip.style.display = 'none';
        });

        settingContainer.appendChild(settingLabel);

        // Check if this is a select box setting
        if (key === 'newSetting') {
            const select = document.createElement('select');
            settings[key].options.forEach(option => {
                const opt = document.createElement('option');
                opt.value = option;
                opt.textContent = option;
                select.appendChild(opt);
            });
            select.value = settings[key].value;
            select.onchange = () => {
                settings[key].value = select.value;
                GM_setValue(key, settings[key].value);
            };
            settingContainer.appendChild(select);
        } else {
            const slider = document.createElement('input');
            slider.type = 'checkbox';
            slider.checked = settings[key].value;
            slider.onchange = () => {
                settings[key].value = slider.checked;
                GM_setValue(key, settings[key].value);
            };
            settingContainer.appendChild(slider);
        }

        column.appendChild(settingContainer);
    }

    // Create setting items and distribute them into columns
    let leftColumnKeys = ['rufont', 'logo', 'translateNames','format'];
    let rightColumnKeys = ['replaceSkins', 'replace', 'newSetting'];

    leftColumnKeys.forEach(key => createSettingItem(key, settings[key].label, settings[key].tooltip, leftColumn));
    rightColumnKeys.forEach(key => createSettingItem(key, settings[key].label, settings[key].tooltip, rightColumn));

    // Clear floats to avoid layout issues
    const clearDiv = document.createElement('div');
    clearDiv.style.clear = 'both';
    container.appendChild(clearDiv);

    // Append the container to the body
    document.body.appendChild(container);

    // Save reference to the settings menu
    settingsMenu = container;
    menuVisible = true;
}

