var replacementLinks = {};

// Функция для замены ссылок
function replaceLinks() {
    if (!settings.replaceSkins.value) return;

    const elements = document.querySelectorAll('[style]');

    elements.forEach(element => {
        let style = element.getAttribute('style');

        // Ищем ссылки в стиле background: url(...)
        const urlPattern = /url\(["']?([^"']+)["']?\)/;
        const match = style.match(urlPattern);

        if (match && match[1] && replacementLinks[match[1]]) {
            // Замена ссылки
            const newUrl = replacementLinks[match[1]];
            style = style.replace(match[1], newUrl);
            element.setAttribute('style', style);
        }
    });
}

// Fetch JSON data and initialize the replacementLinks variable
fetch(jsonUrl)
    .then(response => response.json())
    .then(data => {
        replacementLinks = data;
        console.log('Replacement links loaded:', replacementLinks);
        // Вызов функции замены ссылок после загрузки данных
        replaceLinks();
    })
    .catch(error => {
        console.error('Error loading JSON:', error);
    });


    // Запуск функции замены после загрузки страницы
    window.addEventListener('load', replaceLinks);

		const observer = new MutationObserver(replaceLinks);
		observer.observe(document.body, { childList: true, subtree: true });

function replaceImageSrc() {
    const images = document.querySelectorAll('img[src*="/images/rarity/"]');

    images.forEach(img => {
        const src = img.src;
        const match = src.match(/\/images\/rarity\/([^\/]+)\.png$/);

        if (match && !src.includes('BASE_') && !src.includes('DETERMINATION')) {
            const rarity = match[1];
            const newSrc = src.replace(rarity, `BASE${rarity}`);
            img.src = newSrc;
        }
        if (match && src.includes('DETERMINATION') && !src.includes('УМРЁТЕ')) {
            const rarityContainers = document.querySelectorAll('.rarityImageContainer');

            rarityContainers.forEach(container => {
                const hoverSpan = container.querySelector('.rarityImageHover span span');
                if (hoverSpan) {
                    const rarityText = hoverSpan.textContent.trim();
                    const rarityClass = hoverSpan.className;

                    const newSpan = document.createElement('span');
                    newSpan.className = rarityClass;
                    newSpan.textContent = rarityText;
                if (rarityText === 'УМРЁТЕ'){
                    container.parentNode.replaceChild(newSpan, container);
                }

        }
    });
        }
    });
}

function keepTextOnly() {
    const rarityContainers = document.querySelectorAll('.rarityImageContainer');

    rarityContainers.forEach(container => {
        const hoverSpan = container.querySelector('.rarityImageHover span span');
        if (hoverSpan) {
            const rarityText = hoverSpan.textContent.trim();
            const rarityClass = hoverSpan.className;

            const newSpan = document.createElement('span');
            newSpan.className = rarityClass;
            newSpan.textContent = rarityText;

            container.parentNode.replaceChild(newSpan, container);
        }
    });
}

function reformat() {
    observer1.disconnect();

    var previewElements = document.querySelectorAll('div[style*="font-size:"]');

    if (previewElements.length === 0) {
        console.log('No preview elements found.');
        observer1.observe(document.body, { childList: true, subtree: true });
        return;
    }

    previewElements.forEach(function(previewElement) {
        if (previewElement.closest('#AlertToast')) {
            return;
        }
        if (previewElement.closest('.cardHover')) {
            return;
        }
        if (previewElement.closest('.cardName')) {
            return;
        }
        if (previewElement.closest('[style*="z-index: 1220"]')) {
            return;
        }
      //  if (previewElement.querySelector('[onmouseleave="removeCardHover();"]')) {
      //      return;
      //  }

        // Проверяем значение атрибута neko в HTML-контенте
        var nekoValue = previewElement.outerHTML.match(/neko="(\d+)"/);
        var currentNeko = nekoValue ? parseInt(nekoValue[1]) : 0;

        if (currentNeko >= 2) {
            return; // Пропускаем элемент, если он уже обработан два раза
        }

        currentNeko += 1; // neko

        var start = previewElement;
        var previewText = previewElement.innerHTML.replace(/^<div[^>]*>(.*?)<\/div>$/, '');
        previewText = previewText.replace(/(?<!\\)'/g, "\\'");

        var $htmlCard = $('<div class="card" id="card1">' +
                            '<div class="cardDesc">' +
                                '<div></div>' +
                            '</div>' +
                        '</div>');

        var $cardDescDiv = $htmlCard.find('.cardDesc div');

        $cardDescDiv.html(previewText);

        var size = getResizedFontSize($cardDescDiv, 81);

        var previewData = previewText;
        var htmlContent = '<div neko="' + currentNeko + '" style="font-size: ' + size + 'px;">' + previewData + '</div>';
        previewElement.outerHTML = htmlContent;
    });

    observer1.observe(document.body, { childList: true, subtree: true });
}






